import React, { useState, useEffect } from "react";
import Banner from "../banner/Banner";
import Container from "../container/Container";
import Navbar from "../navbar/Navbar";
import axios from "axios";

function Home() {
  const [location, setLocation] = useState([]);
  const [mealtype, setMealtype] = useState([]);

  useEffect(() => {
    axios
      .get("./location")
      .then((res) => {
        const data = res.data;
        console.log(data);
        setLocation(data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  useEffect(() => {
    axios
      .get("./mealtype")
      .then((res) => {
        const data = res.data;
        setMealtype(data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);
  return (
    <>
      <Navbar />

      <Banner locationData={location} />
      <Container mealtypesData={mealtype} />
      {/* <Cards /> */}
    </>
  );
}
export default Home;

