import React from "react";
import "./Cards.css";
import { useNavigate } from "react-router-dom";

function Cards(props) {
  const { mealtype, mealtypeId, content, image } = props.data;
  const navigate = useNavigate();

  const showFilter = (mealtypeId) => {
    navigate(`/search_filter?mealtype=${mealtypeId}`);
  };
  return (
    <div className="cards col-lg-4 col-md-4 col-sm-4 col-10"style={{cursor:"pointer"}}>
      <div class="item">
        <div class="item1" onClick={() => showFilter(mealtypeId)}>
          <div class="item-left">
            <img
              className="dish-img"
              src={`./images/${image}`}
              alt="breakfast"
              srcset=""
            ></img>
          </div>

          <div class="item-right">
            <h4>{mealtype}</h4>
            <p class="item-right-para">{content}</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Cards;
