import React, { useState, useEffect } from "react";
import Banner from "../banner/Banner";

import Container from "../container/Container";
import Navbar from "../navbar/Navbar";
import axios from "axios";

function Home() {
  const [location, setLocation] = useState([]);
  const [mealtype, setMealtype] = useState([]);

  useEffect(() => {
    axios
      .get("./server/location")
      .then((res) => {
        const data = res.data;
        console.log(data);
        setLocation(data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  useEffect(() => {
    axios
      .get("./server/mealtype")
      .then((res) => {
        const data = res.data;
        setMealtype(data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);
  return (
    <>
      <Navbar />
      <>
        <div>
          <img
            className="headerImg"
            src="./images/81f3ff974d82520780078ba1cfbd453a1583259680.avif"
            alt=""
            srcSet=""
          ></img>

          <h1 className=" headerTitle">Zomato</h1>

          <h1 className="headerDesc">
            Discover the best food & drinks in your place
          </h1>
        </div>

        <div class="searchArea">
          <select
            class="form-select locationDropdown"
            aria-label="Default select example"
          >
            <option selected>Select Your Location</option>
            {location.map((item) => {
              return (
                <option value={item.locationId}>{`${item.location}`}</option>
              );
            })}
          </select>

          <div>
            <input
              class="resInput"
              type="text"
              placeholder="Enter Restaurant Name"
            />
          </div>
        </div>
      </>
      <div className="container-head">
        <h2 className="searchTitle">Quick Searches</h2>
        <h3 className="searchPara">Discover restaurants by type of meal</h3>
      </div>
      {mealtype.map((item) => {
        return <Cards data={item} />;
      })}
      {/* <Banner locationData={location} /> */}
      {/* <Container mealtypesData={mealtype} /> */}
      {/* <Cards /> */}
    </>
  );
}
export default Home;

