import React from "react";
import '../Styles/Details.css';
import queryString from 'query-string';
import axios from 'axios';
import Modal from 'react-modal';
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from 'react-responsive-carousel';

const customStyles = {
    content: {
      top: '50%',
      left: '50%',
      right: 'auto',
      bottom: 'auto',
      marginRight: '-50%',
      transform: 'translate(-50%, -50%)',
    },
};

class Details extends React.Component {
    constructor() {
        super();
        this.state = {
            restaurant: {},
            resId: undefined,
            galleryModalIsOpen: false,
            menuItemsModalIsOpen: false,
            menuItems: []
        }
    }

    componentDidMount() {
        const qs = queryString.parse(window.location.search);
        const { restaurant } = qs;

        axios({
            url: `http://localhost:5500/restaurant/${restaurant}`,
            method: 'GET',
            headers: { 'Content-Type': 'application/json' }
        })
            .then(res => {
                this.setState({ restaurant: res.data.restaurant, resId: restaurant })
            })
            .catch(err => console.log(err))

    }

    handleModal = (state, value) => {
        const { resId } = this.state;
        if (state == "menuItemsModalIsOpen" && value == true){
            axios({
                url: `http://localhost:5500/menuitems/${resId}`,
                method: 'GET',
                headers: { 'Content-Type': 'application/json' }
            })
                .then(res => {
                    this.setState({ menuItems: res.data.restaurant })
                })
                .catch(err => console.log(err))
        }
        this.setState({ [state]: value });
    }

    render() {
        const { restaurant, galleryModalIsOpen, menuItemsModalIsOpen, menuItems } = this.state;
        return (
            <div>
                <nav className="navbar navbar-expand-sm navbar-dark bg-danger">
                    <div className="container-fluid">
                        <div className="logo_filter">
                            <b>e!</b>
                        </div>
                        <form className="d-flex">
                            <a className="nav-link active text-white" aria-current="page" href="#">login</a>

                            <button className="btn btn-danger border-white" type="button">Create an account</button>
                        </form>
                    </div>
                </nav>
                <div className="bodyDetail">
                    <div>
                        <img src={`./imges/${restaurant.image}`} alt="No Image, Sorry for the Inconvinience" width="100%" height="350" />
                        <button className="button" onClick={() => this.handleModal('galleryModalIsOpen', true)}>Click to see Image Gallery</button>
                    </div>
                    <div className="heading">{restaurant.name}</div>
                    <button className="btn-order" onClick={() => this.handleModal('menuItemsModalIsOpen', true)}>Place Online Order</button>

                    <div className="tabs">
                        <div className="tab">
                            <input type="radio" id="tab-1" name="tab-group-1" checked />
                            <label for="tab-1">Overview</label>

                            <div className="content">
                                <div className="about">About this place</div>
                                <div className="head">Cuisine</div>
                                <div className="value">{restaurant && restaurant.cuisine && restaurant.cuisine.map(cuisine => `${cuisine.name}, `)}</div>
                                <div className="head">Average Cost</div>
                                <div className="value">&#8377; {restaurant.min_price} for two people(approx)</div>
                            </div>
                        </div>

                        <div className="tab">
                            <input type="radio" id="tab-2" name="tab-group-1" />
                            <label for="tab-2">Contact</label>
                            <div className="content">
                                <div className="head">Phone Number</div>
                                <div className="value">{restaurant.contact_number}</div>
                                <div className="head">{restaurant.name}</div>
                                <div className="value">{`${restaurant.locality}, ${restaurant.city}`}</div>
                            </div>
                        </div>
                    </div>
                </div>
                <Modal
                    isOpen={galleryModalIsOpen}
                    style={customStyles} >
                    <div className="glyphicon glyphicon-remove" style={{ float: 'right', margin: '0px 0px 10px 0px'}} onClick={() => this.handleModal('galleryModalIsOpen', false)}></div>
                    
                    <Carousel showThumbs={false} showStatus={false}>
                        { restaurant?.thumb?.map((item) => {
                            return (
                                <div>
                                    <img src={`./Img/${item}`} />
                                </div>
                            ) 
                        })}
                        
                    </Carousel>       
                        
                </Modal>
                <Modal
                    isOpen={menuItemsModalIsOpen}
                    style={customStyles} >
                        <div className="glyphicon glyphicon-remove" style={{ float: 'right', margin: '0px 0px 10px 0px'}} onClick={() => this.handleModal('menuItemsModalIsOpen', false)}></div>
                        <div>
                            <div className="heading">{restaurant.name}</div>
                            {menuItems.map((item) => {
                                return(
                                    <div style={{with: '50rem'}}>
                                        <h4>{item.name}</h4> <br />
                                        <h6>{item.description}</h6> <br />
                                    </div>
                                )
                            })}
                        </div>
                </Modal>
            </div>
        )
    }
}

export default Details;





