import React, { useEffect, useState } from "react";
import "./Banner.css";

function Banner({locationData}) {

  const [restaurant, setRestaurant] = useState([]);
  const [inputText, setInputText] = useState(undefined);
  const [suggestions, setSuggestions] = useState([]);

  const  handleLocationChange  = (event) => {
    useEffect(() => {
      axios
        .get("./restaurant")
        .then((res) => {
          const data = res.data;
          console.log(data);
          setRestaurant(data.restaurant);
        })
        .catch((err) => {
          console.log(err);
        });
    }, []);
  }
  return(
    <>
        <div>
          <img
            className="headerImg"
            src="./images/81f3ff974d82520780078ba1cfbd453a1583259680.avif"
            alt=""
            srcset=""
          ></img>

          <h1 className=" headerTitle">Zomato</h1>

          <h1 className="headerDesc">
            Discover the best food & drinks in your place
          </h1>
        </div>

        <div class="searchArea">
          <select
            class="form-select locationDropdown"
            aria-label="Default select example"
          >
            <option selected>Select Your Location</option>
            {locationData.map((item, index) => {
              return (
                <option value={item.locationId} key={index}>{`${item.location}`}</option>
              );
            })}

          </select>

          <div>

            <input
              class="resInput"
              type="text"
              placeholder="Enter Restaurant Name"
            />
          </div>
        </div>
      </>
  )
}

export default Banner

