import React, { useState, useEffect } from "react";
import Banner from "../banner/Banner";
import Container from "../container/Container";
//import Cards from "../cards/Cards";
import Navbar from "../navbar/Navbar";
import axios from "axios";

function Home() {
  const [locations, setLocations] = useState([]);
  const [mealtype, setMealtype] = useState([]);
  const [user, setUser] = useState(null);

  useEffect(() => {
      const getUser = () => {
        fetch("http://localhost:5500/login/success", {
          method: "GET",
          credentials: "include",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
            "Access-Control-Allow-Credentials": true,
          },
        })
          .then((response) => {
            if (response.status === 200) return response.json();
            throw new Error("authentication has been failed!");
          })
          .then((resObject) => {
            setUser(resObject.user);
          })
          .catch((err) => {
            console.log(err);
          });
      };
      getUser();
  }, []);

  useEffect(() => {
    axios
      .get("./location")
      .then((res) => {
        const data = res.data;
        // console.log(data);
        setLocations(data.location);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  useEffect(() => {
    axios
      .get("./mealtype")
      .then((res) => {
        const data = res.data;
        // console.log(data);
        setMealtype(data.mealtype);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);
  return (
    <>
      <Navbar user={user}/>

      <Banner locationData={locations} />
      <Container mealtypesData={mealtype} />
      {/* <Cards /> */}
      {/* <>
        <div>
          <img
            className="headerImg"
            src="./images/81f3ff974d82520780078ba1cfbd453a1583259680.avif"
            alt=""
            srcset=""
          ></img>

          <h1 className=" headerTitle">Zomato</h1>

          <h1 className="headerDesc">
            Discover the best food & drinks in your place
          </h1>
        </div>

        <div class="searchArea">
          <select
            class="form-select locationDropdown"
            aria-label="Default select example"
          >
            <option selected>Select Your Location</option>
            {locations?.map((item, index) => {
              return (
                <option value={item.locationId} key={index.id}>{`${item.location}`}</option>
              );
            })}
          </select>

          <div>
            <input
              class="resInput"
              type="text"
              placeholder="Enter Restaurant Name"
            />
          </div>
        </div>
        <>
          <div className="container-head">
            <h2 className="searchTitle">Quick Searches</h2>
            <h3 className="searchPara">Discover restaurants by type of meal</h3>
          </div>
          {mealtype.map((item, index) => {
            return <Cards data={item} key={index.id}/>;
          })}
        </>
      </> */}
    </>
  );
}
export default Home;

